            Files.copy(
                    inputStream,
                    Paths.get(sonarJavaPluginFileName),
                    StandardCopyOption.REPLACE_EXISTING);
            return new SonarJavaJarHolder(new File(sonarJavaPluginFileName).toPath(), true);
