            plugin.sendMessage(src, "no-valid-url");
        }

        return CommandResult.success();
    }

    @Override
    public CommandSpec buildSpec() {
        return CommandSpec.builder()
                .executor(this)
                .arguments(string(of("url")))
                .permission(PomData.ARTIFACT_ID + ".command.skinupload.base")
                .build();
    }
}

