import java.io.Serializable;
import java.util.ArrayList;
import java.util.List;
import javax.inject.Named;
import javax.mvc.RedirectScoped;

/**
 * Used to transport messages back to the client.
 *
 * @author Hantsy Bai<hantsy@gmail.com>
 */
@RedirectScoped
@Named("flashMessage")
public class AlertMessage implements Serializable {

    private static final long serialVersionUID = 1L;

    public enum Type {
        success, warning, danger, info;
    }

    private Type type = Type.info;
    private String text;
    private String code;

    public AlertMessage() {
    }

    public AlertMessage(Type type, String text) {
        this.type = type;
        this.text = text;
    }

    public AlertMessage(Type type, String code, String message) {
        this.type = type;
        this.code = code;
        this.text = message;
    }

    public String getText() {
        return text;
    }

    public Type getType() {
        return type;
    }

    public String getCode() {
        return code;
    }

    public void notify(Type type, String text) {
        this.type = type;
        this.text = text;
    }

    public static AlertMessage success(String text) {
        return new AlertMessage(Type.success, text);
    }

    public static AlertMessage warning(String text) {
        return new AlertMessage(Type.warning, text);
    }

    public static AlertMessage danger(String text) {
        return new AlertMessage(Type.danger, text);
    }

    public static AlertMessage info(String text) {
        return new AlertMessage(Type.info, text);
    }

    private List<Error> errors = new ArrayList<>();

    public List<Error> getErrors() {
        return errors;
    }

    public void setErrors(List<Error> errors) {
        this.errors = errors;
    }

    public void addError(String field, String code, String message) {
        this.errors.add(new Error(field, code, message));
    }

    public static class Error {

        private String code;
        private String message;
        private String field;

        public Error(){}

        private Error(String field, String code, String message) {
            this.field = field;
            this.code = code;
            this.message = message;
        }

        public String getCode() {
            return code;
        }

        public void setCode(String code) {
            this.code = code;
        }

        public String getMessage() {
            return message;
        }

        public void setMessage(String message) {
            this.message = message;
        }

        public String getField() {
            return field;
        }

        public void setField(String field) {
            this.field = field;
        }

    }

}

package com.example.web;

import java.io.Serializable;
import java.util.Objects;
import javax.mvc.binding.MvcBinding;
import javax.validation.constraints.NotBlank;
import javax.validation.constraints.Size;
import javax.ws.rs.FormParam;

public class TaskForm implements Serializable {

    private static final long serialVersionUID = 1L;

    @FormParam("id")
    private Long id;

    @NotBlank
    @FormParam("name")
    @MvcBinding
    private String name;

    @NotBlank
    @Size(min = 10, max = 2000)
    @FormParam("description")
    @MvcBinding
    private String description;

    public Long getId() {
        return id;
    }

    public void setId(Long id) {
        this.id = id;
    }

    public String getName() {
        return name;
    }

    public void setName(String name) {
        this.name = name;
    }

    public String getDescription() {
        return description;
    }

    public void setDescription(String description) {
        this.description = description;
    }

    @Override
    public int hashCode() {
        int hash = 7;
        hash = 61 * hash + Objects.hashCode(this.id);
        hash = 61 * hash + Objects.hashCode(this.name);
        hash = 61 * hash + Objects.hashCode(this.description);
        return hash;
    }

    @Override
    public boolean equals(Object obj) {
        if (this == obj) {
            return true;
        }
        if (obj == null) {
            return false;
        }
        if (getClass() != obj.getClass()) {
            return false;
        }
        final TaskForm other = (TaskForm) obj;
        if (!Objects.equals(this.name, other.name)) {
            return false;
        }
        if (!Objects.equals(this.description, other.description)) {
            return false;
        }
        if (!Objects.equals(this.id, other.id)) {
            return false;
        }
        return true;
    }

    @Override
    public String toString() {
        return "TaskForm{" + "id=" + id + ", name=" + name + ", description=" + description + '}';
    }

}

