				/* Add Glow Effect */
				ArrayList<String> lore = new ArrayList<>();
				currentLangMeta.addItemFlags(ItemFlag.HIDE_ENCHANTS);
				if (selectedSourceLanguage.equals(main.getSupportedTranslatorLanguages().get(i).getLangCode())) {
