    @Test
    void shouldHandleDisputeEvidenceSubmittedEvent() throws QueueException {
        var mockQueueMessage = mock(QueueMessage.class);
        disputeEvent = anEventFixture()
                .withEventType(EventType.DISPUTE_EVIDENCE_SUBMITTED.name())
